import React from 'react';
import { Navigate } from 'react-router-dom';

const RutaAdmin = ({ children }) => {
  // Temporalmente, simulamos que el usuario es administrador
  // Esto se reemplazará con el contexto real de autenticación más adelante
  const esAdmin = true;

  if (!esAdmin) {
    return <Navigate to="/dashboard" />;
  }

  return children;
};

export default RutaAdmin;
