import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Table, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { servicioUsuarios, servicioTareas, servicioProyectos } from '../../servicios/api';
import './Dashboard.css';

const Dashboard = () => {
  // Estado para almacenar datos del dashboard
  const [estadisticas, setEstadisticas] = useState({
    clientes: 0,
    trabajadores: 0,
    proyectos: 0,
    tareas: 0,
    ingresos: 0
  });

  const [clientesRecientes, setClientesRecientes] = useState([]);
  const [trabajadoresRecientes, setTrabajadoresRecientes] = useState([]);
  const [proyectosRecientes, setProyectosRecientes] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [mensaje, setMensaje] = useState({ tipo: '', texto: '' });

  // Cargar datos del dashboard desde la API
  const cargarDatosDashboard = async () => {
    setCargando(true);
    try {
      // Cargar clientes
      const clientes = await servicioUsuarios.obtenerTodos({ tipo: 'cliente' });
      setClientesRecientes(clientes.slice(0, 4));

      // Cargar trabajadores
      const trabajadores = await servicioUsuarios.obtenerTodos({ tipo: 'trabajador' });
      setTrabajadoresRecientes(trabajadores.slice(0, 3));

      // Cargar proyectos
      const proyectos = await servicioProyectos.obtenerTodos();
      setProyectosRecientes(proyectos.slice(0, 3));

      // Cargar tareas
      const tareas = await servicioTareas.obtenerTodas();

      // Calcular ingresos totales de proyectos
      const ingresosTotales = proyectos.reduce((total, proyecto) => total + (proyecto.presupuesto || 0), 0);

      // Actualizar estadísticas
      setEstadisticas({
        clientes: clientes.length,
        trabajadores: trabajadores.length,
        proyectos: proyectos.length,
        tareas: tareas.length,
        ingresos: ingresosTotales
      });
    } catch (error) {
      console.error("Error al cargar datos del dashboard:", error);
      setMensaje({
        tipo: 'danger',
        texto: 'Error al cargar los datos del dashboard. Por favor, inténtalo de nuevo.'
      });

      // Datos de ejemplo en caso de error
      setEstadisticas({
        clientes: 156,
        trabajadores: 24,
        proyectos: 42,
        tareas: 89,
        ingresos: 24500
      });

      setClientesRecientes([
        { id: 1, nombre: 'Empresa ABC', email: 'contacto@abc.com', fecha: '05/04/2025', estado: 'Activo' },
        { id: 2, nombre: 'Corporación XYZ', email: 'info@xyz.com', fecha: '03/04/2025', estado: 'Activo' },
        { id: 3, nombre: 'Industrias 123', email: 'ventas@123.com', fecha: '01/04/2025', estado: 'Pendiente' },
        { id: 4, nombre: 'Servicios Pro', email: 'admin@pro.com', fecha: '29/03/2025', estado: 'Activo' }
      ]);

      setTrabajadoresRecientes([
        { id: 1, nombre: 'Ana Martínez', email: 'ana@alhambra.com', departamento: 'Ventas', estado: 'Activo' },
        { id: 2, nombre: 'Luis García', email: 'luis@alhambra.com', departamento: 'Soporte', estado: 'Activo' },
        { id: 3, nombre: 'Elena Ruiz', email: 'elena@alhambra.com', departamento: 'Desarrollo', estado: 'Inactivo' }
      ]);

      setProyectosRecientes([
        { id: 1, nombre: 'Desarrollo de aplicación web', cliente: 'Empresa ABC', estado: 'En progreso', progreso: 45 },
        { id: 2, nombre: 'Rediseño de marca', cliente: 'Corporación XYZ', estado: 'Completado', progreso: 100 },
        { id: 3, nombre: 'Campaña de marketing digital', cliente: 'Industrias 123', estado: 'Pendiente', progreso: 10 }
      ]);
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarDatosDashboard();
  }, []);

  // Obtener clase para el estado
  const obtenerClaseEstado = (estado) => {
    switch(estado) {
      case 'Activo':
        return 'bg-success';
      case 'Pendiente':
        return 'bg-warning';
      case 'Inactivo':
        return 'bg-danger';
      case 'Completado':
        return 'bg-success';
      case 'En progreso':
        return 'bg-primary';
      default:
        return 'bg-secondary';
    }
  };

  return (
    <div className="pagina-dashboard-admin">
      <div className="cabecera-dashboard">
        <Container>
          <h1>Panel de Administración</h1>
          <p>Gestiona todos los aspectos de AlhambraCRM</p>
        </Container>
      </div>

      <Container className="contenido-dashboard">
        {mensaje.tipo && (
          <Alert variant={mensaje.tipo} onClose={() => setMensaje({ tipo: '', texto: '' })} dismissible>
            {mensaje.texto}
          </Alert>
        )}

        {cargando ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Cargando...</span>
            </div>
            <p className="mt-3">Cargando datos del dashboard...</p>
          </div>
        ) : (
          <>
            <Row className="mb-4">
              <Col lg={3} md={6} className="mb-4 mb-lg-0">
                <Card className="tarjeta-estadistica">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="texto-secundario mb-1">Clientes</h6>
                        <h3 className="mb-0">{estadisticas.clientes}</h3>
                      </div>
                      <div className="icono-estadistica bg-light-primary">
                        <i className="bi bi-people"></i>
                      </div>
                    </div>
                    <div className="mt-3">
                      <Link to="/admin/clientes" className="enlace-dashboard">
                        Ver todos <i className="bi bi-arrow-right"></i>
                      </Link>
                    </div>
                  </Card.Body>
                </Card>
              </Col>

              <Col lg={3} md={6} className="mb-4 mb-lg-0">
                <Card className="tarjeta-estadistica">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="texto-secundario mb-1">Trabajadores</h6>
                        <h3 className="mb-0">{estadisticas.trabajadores}</h3>
                      </div>
                      <div className="icono-estadistica bg-light-success">
                        <i className="bi bi-person-badge"></i>
                      </div>
                    </div>
                    <div className="mt-3">
                      <Link to="/admin/trabajadores" className="enlace-dashboard">
                        Ver todos <i className="bi bi-arrow-right"></i>
                      </Link>
                    </div>
                  </Card.Body>
                </Card>
              </Col>

              <Col lg={3} md={6} className="mb-4 mb-lg-0">
                <Card className="tarjeta-estadistica">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="texto-secundario mb-1">Proyectos</h6>
                        <h3 className="mb-0">{estadisticas.proyectos}</h3>
                      </div>
                      <div className="icono-estadistica bg-light-info">
                        <i className="bi bi-folder"></i>
                      </div>
                    </div>
                    <div className="mt-3">
                      <Link to="/admin/proyectos" className="enlace-dashboard">
                        Ver todos <i className="bi bi-arrow-right"></i>
                      </Link>
                    </div>
                  </Card.Body>
                </Card>
              </Col>

              <Col lg={3} md={6}>
                <Card className="tarjeta-estadistica">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <h6 className="texto-secundario mb-1">Ingresos</h6>
                        <h3 className="mb-0">{estadisticas.ingresos.toLocaleString()}€</h3>
                      </div>
                      <div className="icono-estadistica bg-light-warning">
                        <i className="bi bi-currency-euro"></i>
                      </div>
                    </div>
                    <div className="mt-3">
                      <Link to="/admin/informes" className="enlace-dashboard">
                        Ver informes <i className="bi bi-arrow-right"></i>
                      </Link>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            <Row>
              <Col lg={6} className="mb-4">
                <Card className="tarjeta-dashboard h-100">
                  <Card.Header className="d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">Clientes Recientes</h5>
                    <Link to="/admin/clientes" className="btn btn-sm btn-outline-primary">
                      Ver Todos
                    </Link>
                  </Card.Header>
                  <Card.Body>
                    <div className="tabla-responsive">
                      <Table hover>
                        <thead>
                          <tr>
                            <th>Nombre</th>
                            <th>Email</th>
                            <th>Estado</th>
                          </tr>
                        </thead>
                        <tbody>
                          {clientesRecientes.map(cliente => (
                            <tr key={cliente.id}>
                              <td>{cliente.nombre}</td>
                              <td>{cliente.email}</td>
                              <td>
                                <span className={`badge ${obtenerClaseEstado(cliente.estado)}`}>
                                  {cliente.estado}
                                </span>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </Table>
                    </div>
                  </Card.Body>
                </Card>
              </Col>

              <Col lg={6} className="mb-4">
                <Card className="tarjeta-dashboard h-100">
                  <Card.Header className="d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">Trabajadores Recientes</h5>
                    <Link to="/admin/trabajadores" className="btn btn-sm btn-outline-primary">
                      Ver Todos
                    </Link>
                  </Card.Header>
                  <Card.Body>
                    <div className="tabla-responsive">
                      <Table hover>
                        <thead>
                          <tr>
                            <th>Nombre</th>
                            <th>Departamento</th>
                            <th>Estado</th>
                          </tr>
                        </thead>
                        <tbody>
                          {trabajadoresRecientes.map(trabajador => (
                            <tr key={trabajador.id}>
                              <td>{trabajador.nombre}</td>
                              <td>{trabajador.departamento}</td>
                              <td>
                                <span className={`badge ${obtenerClaseEstado(trabajador.estado)}`}>
                                  {trabajador.estado}
                                </span>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </Table>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            <Row>
              <Col lg={12}>
                <Card className="tarjeta-dashboard">
                  <Card.Header className="d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">Proyectos Recientes</h5>
                    <Link to="/admin/proyectos" className="btn btn-sm btn-outline-primary">
                      Ver Todos
                    </Link>
                  </Card.Header>
                  <Card.Body>
                    <div className="tabla-responsive">
                      <Table hover>
                        <thead>
                          <tr>
                            <th>Nombre</th>
                            <th>Cliente</th>
                            <th>Estado</th>
                            <th>Progreso</th>
                          </tr>
                        </thead>
                        <tbody>
                          {proyectosRecientes.map(proyecto => (
                            <tr key={proyecto.id}>
                              <td>{proyecto.nombre}</td>
                              <td>{proyecto.cliente}</td>
                              <td>
                                <span className={`badge ${obtenerClaseEstado(proyecto.estado)}`}>
                                  {proyecto.estado}
                                </span>
                              </td>
                              <td>
                                <div className="progress" style={{ height: '8px' }}>
                                  <div 
                                    className={`progress-bar bg-${proyecto.estado === 'Completado' ? 'success' : proyecto.estado === 'En progreso' ? 'primary' : 'warning'}`}
                                    role="progressbar" 
                                    style={{ width: `${proyecto.progreso}%` }}
                                    aria-valuenow={proyecto.progreso}
                                    aria-valuemin="0" 
                                    aria-valuemax="100"
                                  ></div>
                                </div>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </Table>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
