import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Container, Navbar, Nav, Button } from 'react-bootstrap';
import './Cabecera.css';

const Cabecera = () => {
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Navbar 
      expand="lg" 
      fixed="top" 
      className={`cabecera ${scrolled ? 'cabecera-scrolled' : ''}`}
    >
      <Container>
        <Navbar.Brand as={Link} to="/" className="logo">
          <span className="logo-text">AlhambraCRM</span>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link 
              as={Link} 
              to="/" 
              className={location.pathname === '/' ? 'active' : ''}
            >
              Inicio
            </Nav.Link>
            <Nav.Link 
              as={Link} 
              to="/caracteristicas" 
              className={location.pathname === '/caracteristicas' ? 'active' : ''}
            >
              Características
            </Nav.Link>
            <Nav.Link 
              as={Link} 
              to="/precios" 
              className={location.pathname === '/precios' ? 'active' : ''}
            >
              Precios
            </Nav.Link>
            <Nav.Link 
              as={Link} 
              to="/contacto" 
              className={location.pathname === '/contacto' ? 'active' : ''}
            >
              Contacto
            </Nav.Link>
          </Nav>
          <div className="d-flex">
            <Link to="/login" className="me-3">
              <Button variant="outline-primary" className="boton-bordeado">
                Iniciar Sesión
              </Button>
            </Link>
            <Link to="/registro">
              <Button variant="primary" className="boton-verde">
                Registrarse
              </Button>
            </Link>
          </div>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Cabecera;
