import React, { createContext, useState, useEffect } from 'react';

// Crear el contexto
export const ContextoAutenticacion = createContext();

export const ProveedorAutenticacion = ({ children }) => {
  const [usuario, setUsuario] = useState(null);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    // Comprobar si hay un usuario en localStorage
    const usuarioGuardado = localStorage.getItem('usuario');
    if (usuarioGuardado) {
      setUsuario(JSON.parse(usuarioGuardado));
    }
    setCargando(false);
  }, []);

  // Función para iniciar sesión
  const iniciarSesion = (datosUsuario, callback) => {
    // Aquí se implementará la lógica real de autenticación con el backend
    // Por ahora, simulamos un inicio de sesión exitoso
    const usuarioAutenticado = {
      id: 1,
      nombre: 'Usuario',
      apellidos: 'Demo',
      email: datosUsuario.email,
      rol: datosUsuario.email.includes('admin') ? 'admin' : 'cliente'
    };

    setUsuario(usuarioAutenticado);
    localStorage.setItem('usuario', JSON.stringify(usuarioAutenticado));

    if (callback) callback();
  };

  // Función para cerrar sesión
  const cerrarSesion = (callback) => {
    setUsuario(null);
    localStorage.removeItem('usuario');

    if (callback) callback();
  };

  // Verificar si el usuario está autenticado
  const estaAutenticado = () => {
    return !!usuario;
  };

  // Verificar si el usuario es administrador
  const esAdmin = () => {
    return usuario && usuario.rol === 'admin';
  };

  // Valores que se proporcionarán a través del contexto
  const valor = {
    usuario,
    cargando,
    iniciarSesion,
    cerrarSesion,
    estaAutenticado,
    esAdmin
  };

  return (
    <ContextoAutenticacion.Provider value={valor}>
      {children}
    </ContextoAutenticacion.Provider>
  );
};
