import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import './SeccionCaracteristicas.css';

const SeccionCaracteristicas = () => {
  const caracteristicas = [
    {
      icono: 'app-developer-icon-small-01.png',
      titulo: 'Gestión de Contactos',
      descripcion: 'Organiza y accede fácilmente a toda la información de tus clientes en un solo lugar.'
    },
    {
      icono: 'app-developer-icon-small-02.png',
      titulo: 'Seguimiento de Ventas',
      descripcion: 'Visualiza y gestiona tu pipeline de ventas para cerrar más acuerdos.'
    },
    {
      icono: 'app-developer-icon-small-03.png',
      titulo: 'Automatización de Marketing',
      descripcion: 'Crea campañas de email marketing y automatiza el seguimiento de leads.'
    },
    {
      icono: 'app-developer-icon-small-04.png',
      titulo: 'Atención al Cliente',
      descripcion: 'Gestiona tickets de soporte y mejora la satisfacción de tus clientes.'
    },
    {
      icono: 'app-developer-icon-small-05.png',
      titulo: 'Informes y Análisis',
      descripcion: 'Obtén insights valiosos con informes personalizables y dashboards interactivos.'
    },
    {
      icono: 'app-developer-icon-small-07.png',
      titulo: 'Integraciones',
      descripcion: 'Conecta con tus herramientas favoritas para un flujo de trabajo sin interrupciones.'
    }
  ];

  return (
    <section className="seccion-caracteristicas">
      <Container>
        <div className="texto-central mb-5">
          <h2 className="titulo-seccion">Características Principales</h2>
          <p className="subtitulo-seccion">
            AlhambraCRM ofrece todas las herramientas que necesitas para gestionar tus relaciones con clientes de manera efectiva.
          </p>
        </div>

        <Row>
          {caracteristicas.map((caracteristica, index) => (
            <Col lg={4} md={6} className="mb-4" key={index}>
              <Card className="tarjeta-caracteristica">
                <div className="icono-caracteristica">
                  <img 
                    src={`/assets/imgs/${caracteristica.icono}`} 
                    alt={caracteristica.titulo} 
                  />
                </div>
                <Card.Body>
                  <Card.Title className="titulo-caracteristica">{caracteristica.titulo}</Card.Title>
                  <Card.Text className="descripcion-caracteristica">
                    {caracteristica.descripcion}
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default SeccionCaracteristicas;
