import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Table, Form, InputGroup, Modal, Badge } from 'react-bootstrap';
import { servicioInformes } from '../../servicios/api';
import './Informes.css';

const Informes = () => {
  // Estado para almacenar la lista de informes
  const [informes, setInformes] = useState([]);
  const [filtroTitulo, setFiltroTitulo] = useState('');
  const [filtroTipo, setFiltroTipo] = useState('todos');
  const [cargando, setCargando] = useState(true);
  const [vistaActiva, setVistaActiva] = useState('tabla');

  // Estados para modales
  const [mostrarModalEditar, setMostrarModalEditar] = useState(false);
  const [mostrarModalNuevo, setMostrarModalNuevo] = useState(false);
  const [informeActual, setInformeActual] = useState(null);
  const [formDatos, setFormDatos] = useState({
    titulo: '',
    descripcion: '',
    tipo: 'Ventas',
    fechaCreacion: '',
    fechaActualizacion: '',
    autor: '',
    proyecto: '',
    contenido: ''
  });

  // Cargar informes desde la API
  const cargarInformes = async () => {
    setCargando(true);
    try {
      // En un entorno real, descomentar esta línea para obtener datos de la API
      // const datos = await servicioInformes.obtenerTodos();
      // setInformes(datos);

      // Datos de ejemplo para desarrollo
      const informesMock = [
        {
          id: 1,
          titulo: 'Informe de ventas Q1 2025',
          descripcion: 'Análisis detallado de las ventas del primer trimestre de 2025',
          tipo: 'Ventas',
          fechaCreacion: '2025-04-01',
          fechaActualizacion: '2025-04-05',
          autor: 'María López',
          proyecto: 'Desarrollo de aplicación web',
          contenido: 'Este informe muestra un análisis detallado de las ventas del primer trimestre del año 2025. Se observa un incremento del 15% respecto al mismo período del año anterior.'
        },
        {
          id: 2,
          titulo: 'Informe de gastos operativos',
          descripcion: 'Análisis de los gastos operativos del departamento de marketing',
          tipo: 'Gastos',
          fechaCreacion: '2025-03-15',
          fechaActualizacion: '2025-03-20',
          autor: 'Carlos Ruiz',
          proyecto: 'Campaña de marketing digital',
          contenido: 'Este informe detalla los gastos operativos del departamento de marketing durante el primer trimestre. Se ha conseguido una reducción del 8% en comparación con el presupuesto inicial.'
        },
        {
          id: 3,
          titulo: 'Rendimiento de campaña publicitaria',
          descripcion: 'Análisis del rendimiento de la campaña en redes sociales',
          tipo: 'Rendimiento',
          fechaCreacion: '2025-02-20',
          fechaActualizacion: '2025-02-25',
          autor: 'Laura Martínez',
          proyecto: 'Campaña de marketing digital',
          contenido: 'Este informe analiza el rendimiento de la campaña publicitaria en redes sociales. Se ha alcanzado un ROI del 120% y un incremento del 25% en el engagement.'
        },
        {
          id: 4,
          titulo: 'Informe de actividad de desarrollo',
          descripcion: 'Seguimiento del progreso del equipo de desarrollo',
          tipo: 'Actividad',
          fechaCreacion: '2025-03-25',
          fechaActualizacion: '2025-03-30',
          autor: 'Javier Fernández',
          proyecto: 'Desarrollo de aplicación móvil',
          contenido: 'Este informe muestra el progreso del equipo de desarrollo durante el último mes. Se han completado 45 tareas de las 50 planificadas, lo que representa un 90% de cumplimiento.'
        }
      ];

      setInformes(informesMock);
    } catch (error) {
      console.error("Error al cargar informes:", error);
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarInformes();
  }, []);

  // Filtrar informes según los criterios
  const informesFiltrados = informes.filter(informe => {
    const coincideTitulo = informe.titulo.toLowerCase().includes(filtroTitulo.toLowerCase());
    const coincideTipo = filtroTipo === 'todos' || informe.tipo === filtroTipo;

    return coincideTitulo && coincideTipo;
  });

  // Manejadores de eventos
  const handleFiltroTituloChange = (e) => {
    setFiltroTitulo(e.target.value);
  };

  const handleFiltroTipoChange = (e) => {
    setFiltroTipo(e.target.value);
  };

  const handleNuevoInforme = () => {
    setFormDatos({
      titulo: '',
      descripcion: '',
      tipo: 'Ventas',
      fechaCreacion: new Date().toISOString().split('T')[0],
      fechaActualizacion: new Date().toISOString().split('T')[0],
      autor: '',
      proyecto: '',
      contenido: ''
    });
    setInformeActual(null);
    setMostrarModalNuevo(true);
  };

  const handleEditarInforme = (id) => {
    const informe = informes.find(i => i.id === id);
    if (informe) {
      setInformeActual(informe);
      setFormDatos({
        titulo: informe.titulo,
        descripcion: informe.descripcion,
        tipo: informe.tipo,
        fechaCreacion: informe.fechaCreacion,
        fechaActualizacion: new Date().toISOString().split('T')[0],
        autor: informe.autor,
        proyecto: informe.proyecto,
        contenido: informe.contenido
      });
      setMostrarModalEditar(true);
    }
  };

  const handleEliminarInforme = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este informe?')) {
      try {
        setCargando(true);
        // En un entorno real, descomentar esta línea para eliminar en la API
        // await servicioInformes.eliminar(id);

        // Actualizar la lista de informes
        setInformes(informes.filter(informe => informe.id !== id));

        // Mostrar mensaje de éxito
        alert('Informe eliminado correctamente');
      } catch (error) {
        console.error(`Error al eliminar informe con ID ${id}:`, error);
        alert('Error al eliminar el informe. Por favor, inténtalo de nuevo.');
      } finally {
        setCargando(false);
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormDatos({
      ...formDatos,
      [name]: value
    });
  };

  const handleGuardarInforme = async () => {
    // Validar formulario
    if (!formDatos.titulo || !formDatos.tipo) {
      alert('Por favor, completa los campos obligatorios');
      return;
    }

    try {
      setCargando(true);

      if (informeActual) {
        // Actualizar informe existente
        const datosActualizados = {
          ...formDatos,
          fechaActualizacion: new Date().toISOString().split('T')[0]
        };

        // En un entorno real, descomentar esta línea para actualizar en la API
        // await servicioInformes.actualizar(informeActual.id, datosActualizados);

        // Actualizar la lista de informes
        const informesActualizados = informes.map(i => 
          i.id === informeActual.id ? { 
            ...i, 
            ...datosActualizados
          } : i
        );

        setInformes(informesActualizados);
        setMostrarModalEditar(false);

        // Mostrar mensaje de éxito
        alert('Informe actualizado correctamente');
      } else {
        // Crear nuevo informe
        const nuevoInforme = {
          ...formDatos,
          id: informes.length > 0 ? Math.max(...informes.map(i => i.id)) + 1 : 1,
          fechaCreacion: new Date().toISOString().split('T')[0],
          fechaActualizacion: new Date().toISOString().split('T')[0]
        };

        // En un entorno real, descomentar esta línea para crear en la API
        // const informeCreado = await servicioInformes.crear(nuevoInforme);
        // setInformes([...informes, informeCreado]);

        // Para desarrollo
        setInformes([...informes, nuevoInforme]);
        setMostrarModalNuevo(false);

        // Mostrar mensaje de éxito
        alert('Informe creado correctamente');
      }
    } catch (error) {
      console.error('Error al guardar el informe:', error);
      alert('Error al guardar el informe. Por favor, inténtalo de nuevo.');
    } finally {
      setCargando(false);
    }
  };

  const obtenerClaseTipo = (tipo) => {
    switch(tipo) {
      case 'Ventas':
        return 'tipo-ventas';
      case 'Gastos':
        return 'tipo-gastos';
      case 'Rendimiento':
        return 'tipo-rendimiento';
      case 'Actividad':
        return 'tipo-actividad';
      default:
        return '';
    }
  };

  return (
    <div className="pagina-informes-admin">
      <div className="cabecera-seccion">
        <Container>
          <h1>Gestión de Informes</h1>
          <p>Administra todos los informes de AlhambraCRM</p>
        </Container>
      </div>

      <Container className="contenido-seccion">
        <Card className="tarjeta-filtros mb-4">
          <Card.Body>
            <Row className="align-items-center">
              <Col lg={4} md={6} className="mb-3 mb-lg-0">
                <InputGroup>
                  <InputGroup.Text>
                    <i className="bi bi-search"></i>
                  </InputGroup.Text>
                  <Form.Control
                    placeholder="Buscar por título..."
                    value={filtroTitulo}
                    onChange={handleFiltroTituloChange}
                  />
                </InputGroup>
              </Col>

              <Col lg={3} md={6} className="mb-3 mb-lg-0">
                <Form.Select 
                  value={filtroTipo} 
                  onChange={handleFiltroTipoChange}
                >
                  <option value="todos">Todos los tipos</option>
                  <option value="Ventas">Ventas</option>
                  <option value="Gastos">Gastos</option>
                  <option value="Rendimiento">Rendimiento</option>
                  <option value="Actividad">Actividad</option>
                </Form.Select>
              </Col>

              <Col lg={5} className="text-lg-end">
                <div className="d-flex justify-content-lg-end gap-2">
                  <div className="btn-group me-2">
                    <Button 
                      variant={vistaActiva === 'tabla' ? 'primary' : 'outline-primary'}
                      onClick={() => setVistaActiva('tabla')}
                    >
                      <i className="bi bi-table"></i>
                    </Button>
                    <Button 
                      variant={vistaActiva === 'tarjetas' ? 'primary' : 'outline-primary'}
                      onClick={() => setVistaActiva('tarjetas')}
                    >
                      <i className="bi bi-grid-3x3-gap"></i>
                    </Button>
                  </div>

                  <Button 
                    variant="primary" 
                    className="btn-principal"
                    onClick={handleNuevoInforme}
                  >
                    <i className="bi bi-plus-circle me-2"></i>
                    Nuevo Informe
                  </Button>
                </div>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {vistaActiva === 'tabla' ? (
          <Card className="tarjeta-tabla">
            <Card.Body>
              {cargando ? (
                <div className="text-center py-5">
                  <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Cargando...</span>
                  </div>
                  <p className="mt-3">Cargando informes...</p>
                </div>
              ) : informesFiltrados.length === 0 ? (
                <div className="text-center py-5">
                  <i className="bi bi-search fs-1 text-muted"></i>
                  <p className="mt-3">No se encontraron informes con los filtros aplicados.</p>
                </div>
              ) : (
                <div className="tabla-responsive">
                  <Table hover>
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Título</th>
                        <th>Tipo</th>
                        <th>Autor</th>
                        <th>Proyecto</th>
                        <th>Fecha Creación</th>
                        <th>Fecha Actualización</th>
                        <th>Acciones</th>
                      </tr>
                    </thead>
                    <tbody>
                      {informesFiltrados.map(informe => (
                        <tr key={informe.id}>
                          <td>#{informe.id}</td>
                          <td>{informe.titulo}</td>
                          <td>
                            <Badge className={obtenerClaseTipo(informe.tipo)}>
                              {informe.tipo}
                            </Badge>
                          </td>
                          <td>{informe.autor}</td>
                          <td>{informe.proyecto}</td>
                          <td>{informe.fechaCreacion}</td>
                          <td>{informe.fechaActualizacion}</td>
                          <td>
                            <Button 
                              variant="link" 
                              size="sm" 
                              className="p-0 me-2 btn-accion"
                              onClick={() => handleEditarInforme(informe.id)}
                              title="Editar"
                            >
                              <i className="bi bi-pencil"></i>
                            </Button>
                            <Button 
                              variant="link" 
                              size="sm" 
                              className="p-0 text-danger btn-accion"
                              onClick={() => handleEliminarInforme(informe.id)}
                              title="Eliminar"
                            >
                              <i className="bi bi-trash"></i>
                            </Button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </div>
              )}
            </Card.Body>
          </Card>
        ) : (
          <Row>
            {cargando ? (
              <div className="text-center py-5">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Cargando...</span>
                </div>
                <p className="mt-3">Cargando informes...</p>
              </div>
            ) : informesFiltrados.length === 0 ? (
              <div className="text-center py-5">
                <i className="bi bi-search fs-1 text-muted"></i>
                <p className="mt-3">No se encontraron informes con los filtros aplicados.</p>
              </div>
            ) : (
              informesFiltrados.map(informe => (
                <Col key={informe.id} lg={4} md={6} className="mb-4">
                  <Card className="tarjeta-informe">
                    <Card.Body>
                      <div className="d-flex justify-content-between align-items-start mb-3">
                        <h5 className="mb-0">{informe.titulo}</h5>
                        <Badge className={obtenerClaseTipo(informe.tipo)}>
                          {informe.tipo}
                        </Badge>
                      </div>

                      <p className="texto-descripcion">{informe.descripcion}</p>

                      <div className="info-informe mb-3">
                        <div className="d-flex justify-content-between mb-2">
                          <span className="etiqueta">Autor:</span>
                          <span className="valor">{informe.autor}</span>
                        </div>
                        <div className="d-flex justify-content-between mb-2">
                          <span className="etiqueta">Proyecto:</span>
                          <span className="valor">{informe.proyecto}</span>
                        </div>
                        <div className="d-flex justify-content-between mb-2">
                          <span className="etiqueta">Creado:</span>
                          <span className="valor">{informe.fechaCreacion}</span>
                        </div>
                        <div className="d-flex justify-content-between mb-2">
                          <span className="etiqueta">Actualizado:</span>
                          <span className="valor">{informe.fechaActualizacion}</span>
                        </div>
                      </div>

                      <div className="d-flex gap-2">
                        <Button 
                          variant="outline-primary" 
                          className="w-100"
                          onClick={() => handleEditarInforme(informe.id)}
                        >
                          <i className="bi bi-pencil me-2"></i>Editar
                        </Button>
                        <Button 
                          variant="outline-danger" 
                          className="w-100"
                          onClick={() => handleEliminarInforme(informe.id)}
                        >
                          <i className="bi bi-trash me-2"></i>Eliminar
                        </Button>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              ))
            )}
          </Row>
        )}
      </Container>

      {/* Modal para editar informe */}
      <Modal show={mostrarModalEditar} onHide={() => setMostrarModalEditar(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Editar Informe</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Título del informe</Form.Label>
              <Form.Control 
                type="text" 
                name="titulo"
                value={formDatos.titulo}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Descripción</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={2}
                name="descripcion"
                value={formDatos.descripcion}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Tipo de informe</Form.Label>
                  <Form.Select 
                    name="tipo"
                    value={formDatos.tipo}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="Ventas">Ventas</option>
                    <option value="Gastos">Gastos</option>
                    <option value="Rendimiento">Rendimiento</option>
                    <option value="Actividad">Actividad</option>
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Fecha de creación</Form.Label>
                  <Form.Control 
                    type="date" 
                    name="fechaCreacion"
                    value={formDatos.fechaCreacion}
                    onChange={handleInputChange}
                    disabled
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Autor</Form.Label>
                  <Form.Control 
                    type="text" 
                    name="autor"
                    value={formDatos.autor}
                    onChange={handleInputChange}
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Proyecto relacionado</Form.Label>
                  <Form.Control 
                    type="text" 
                    name="proyecto"
                    value={formDatos.proyecto}
                    onChange={handleInputChange}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Contenido del informe</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={6}
                name="contenido"
                value={formDatos.contenido}
                onChange={handleInputChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setMostrarModalEditar(false)}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleGuardarInforme}>
            Guardar Cambios
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Modal para nuevo informe */}
      <Modal show={mostrarModalNuevo} onHide={() => setMostrarModalNuevo(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Nuevo Informe</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Título del informe</Form.Label>
              <Form.Control 
                type="text" 
                name="titulo"
                value={formDatos.titulo}
                onChange={handleInputChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Descripción</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={2}
                name="descripcion"
                value={formDatos.descripcion}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Tipo de informe</Form.Label>
                  <Form.Select 
                    name="tipo"
                    value={formDatos.tipo}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="Ventas">Ventas</option>
                    <option value="Gastos">Gastos</option>
                    <option value="Rendimiento">Rendimiento</option>
                    <option value="Actividad">Actividad</option>
                  </Form.Select>
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Autor</Form.Label>
                  <Form.Control 
                    type="text" 
                    name="autor"
                    value={formDatos.autor}
                    onChange={handleInputChange}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Proyecto relacionado</Form.Label>
              <Form.Control 
                type="text" 
                name="proyecto"
                value={formDatos.proyecto}
                onChange={handleInputChange}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Contenido del informe</Form.Label>
              <Form.Control 
                as="textarea" 
                rows={6}
                name="contenido"
                value={formDatos.contenido}
                onChange={handleInputChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setMostrarModalNuevo(false)}>
            Cancelar
          </Button>
          <Button variant="primary" onClick={handleGuardarInforme}>
            Crear Informe
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Informes;
